import chalk from "chalk";

const joke = "jeg pleier å be ungene om å stille seg i hjørne om de klager på at det er kaldt, er jo 90 grader der.";

const words = joke.split(" ");

let coloredJoke = "";

for (let i = 0; i < words.length; i++) {
    // Choose a color for each word
    const color = chalk.rgb(getRandomColor(), getRandomColor(), getRandomColor());

    // Apply the color to the word
    coloredJoke += color(words[i]) + " ";
}

console.log(coloredJoke);

// Function to generate a random color component
function getRandomColor() {
    return Math.floor(Math.random() * 256);
}
